/// <reference types="react" />

import React from "react";

export interface CalendarProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 默认选中的日期（moment 对象）
   */
  defaultValue?: any;

  /**
   * 选中的日期值 (moment 对象)
   */
  value?: any;

  /**
   * 面板模式
   */
  mode?: "date" | "month" | "year";

  /**
   * 是否展示非本月的日期
   */
  showOtherMonth?: boolean;

  /**
   * 默认展示的月份
   */
  defaultVisibleMonth?: (() => void);

  /**
   * 展现形态
   */
  shape?: "card" | "fullscreen" | "panel";

  /**
   * 选择日期单元格时的回调
   */
  onSelect?: (() => void);

  /**
   * 自定义样式类
   */
  className?: string;

  /**
   * 自定义日期渲染函数
   */
  dateCellRender?: (() => void);

  /**
   * 自定义月份渲染函数
   */
  monthCellRender?: (() => void);

  /**
   * 不可选择的日期
   */
  disabledDate?: (() => void);

  /**
   * 国际化配置
   */
  locale?: {};
}

export default class Calendar extends React.Component<CalendarProps, any> {}
