/// <reference types="react" />

import React from "react";

export interface RangePickerProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 默认展示的起始月份
   */
  defaultVisibleMonth?: (() => void);

  /**
   * 日期范围值数组 [moment, moment]
   */
  value?: Array<any>;

  /**
   * 初始的日期范围值数组 [moment, moment]
   */
  defaultValue?: any;

  /**
   * 日期格式
   */
  format?: string;

  /**
   * 是否使用时间控件，支持传入 TimePicker 的属性
   */
  showTime?: {} | boolean;

  /**
   * 每次选择是否重置时间（仅在 showTime 开启时有效）
   */
  resetTime?: boolean;

  /**
   * 禁用日期函数
   */
  disabledDate?: (() => void);

  /**
   * 自定义面板页脚
   */
  footerRender?: (() => void);

  /**
   * 日期范围值改变时的回调
   */
  onChange?: (() => void);

  /**
   * 点击确认按钮时的回调
   */
  onOk?: (() => void);

  /**
   * 输入框内置标签
   */
  label?: React.ReactNode;

  /**
   * 输入框状态
   */
  state?: "error" | "success";

  /**
   * 输入框尺寸
   */
  size?: "small" | "medium" | "large";

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 是否显示清空按钮
   */
  hasClear?: boolean;

  /**
   * 弹层显示状态
   */
  visible?: boolean;

  /**
   * 弹层默认是否显示
   */
  defaultVisible?: boolean;

  /**
   * 弹层展示状态变化时的回调
   */
  onVisibleChange?: (() => void);

  /**
   * 弹层触发方式
   */
  popupTriggerType?: "click" | "hover";

  /**
   * 弹层对齐方式
   */
  popupAlign?: string;

  /**
   * 弹层容器
   */
  popupContainer?: (() => void);

  /**
   * 弹层自定义样式
   */
  popupStyle?: {};

  /**
   * 弹层自定义样式类
   */
  popupClassName?: string;

  /**
   * 弹层其他属性
   */
  popupProps?: {};
}

export class RangePicker extends React.Component<RangePickerProps, any> {}
export interface DatePickerProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 输入框内置标签
   */
  label?: React.ReactNode;

  /**
   * 输入框状态
   */
  state?: "success" | "error";

  /**
   * 输入提示
   */
  placeholder?: string;

  /**
   * 默认展现的月
   */
  defaultVisibleMonth?: (() => void);

  /**
   * 日期值（受控）moment 对象
   */
  value?: any;

  /**
   * 初始日期值，moment 对象
   */
  defaultValue?: any;

  /**
   * 日期值的格式（用于限定用户输入和展示）
   */
  format?: string;

  /**
   * 是否使用时间控件，传入 TimePicker 的属性 { defaultValue, format, ... }
   */
  showTime?: {} | boolean;

  /**
   * 每次选择日期时是否重置时间（仅在 showTime 开启时有效）
   */
  resetTime?: boolean;

  /**
   * 禁用日期函数
   */
  disabledDate?: (() => void);

  /**
   * 自定义面板页脚
   */
  footerRender?: (() => void);

  /**
   * 日期值改变时的回调
   */
  onChange?: (() => void);

  /**
   * 点击确认按钮时的回调
   */
  onOk?: (() => void);

  /**
   * 输入框尺寸
   */
  size?: "small" | "medium" | "large";

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 是否显示清空按钮
   */
  hasClear?: boolean;

  /**
   * 弹层显示状态
   */
  visible?: boolean;

  /**
   * 弹层默认是否显示
   */
  defaultVisible?: boolean;

  /**
   * 弹层展示状态变化时的回调
   */
  onVisibleChange?: (() => void);

  /**
   * 弹层触发方式
   */
  popupTriggerType?: "click" | "hover";

  /**
   * 弹层对齐方式
   */
  popupAlign?: string;

  /**
   * 弹层容器
   */
  popupContainer?: (() => void);

  /**
   * 弹层自定义样式
   */
  popupStyle?: {};

  /**
   * 弹层自定义样式类
   */
  popupClassName?: string;

  /**
   * 弹层其他属性
   */
  popupProps?: {};
}

export default class DatePicker extends React.Component<DatePickerProps, any> {
  static RangePicker: typeof RangePicker;
}
