/// <reference types="react" />

import React from "react";

export interface NumberPickerProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 样式前缀
   */
  prefix?: string;

  /**
   * 设置类型
   */
  type?: "normal" | "inline";

  /**
   * 大小
   */
  size?: "large" | "medium";

  /**
   * 当前值
   */
  value?: number;

  /**
   * 默认值
   */
  defaultValue?: any;

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 步长
   */
  step?: number | string;

  /**
   * 保留小数点后位数
   */
  precision?: number;

  /**
   * 用户是否可以输入
   */
  editable?: boolean;

  /**
   * 自动焦点
   */
  autoFocus?: boolean;

  /**
   * 数值被改变的事件
   */
  onChange?: (() => void);

  /**
   * 键盘按下
   */
  onKeyDown?: (() => void);

  /**
   * 焦点获得
   */
  onFocus?: (() => void);

  /**
   * 焦点失去
   */
  onBlur?: (() => void);

  /**
   * 数值订正后的回调
   */
  onCorrect?: (() => void);

  /**
   * 最大值
   */
  max?: number;

  /**
   * 最小值
   */
  min?: number;

  /**
   * 自定义class
   */
  className?: string;

  /**
   * 自定义内联样式
   */
  style?: React.CSSProperties;
}

export default class NumberPicker extends React.Component<
  NumberPickerProps,
  any
> {}
