/// <reference types="react" />

import React from "react";

export interface ParagraphProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 额外的样式名 会附加到 root dom 上
   */
  className?: any;

  /**
   * 什么方式展示段落。 默认：long 展示所有文本，short 展示三行以内（非强制）
   */
  type?: string;

  /**
   * 组件大小。 默认：medium 中号，small 小号
   */
  size?: string;
}

export default class Paragraph extends React.Component<ParagraphProps, any> {}
