/// <reference types="react" />

import React from "react";

export interface RatingProps extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 默认值
   */
  defaultValue?: any;

  /**
   * 值
   */
  value?: number;

  /**
   * 评分的总数
   */
  count?: number;

  /**
   * 是否显示 grade
   */
  showGrade?: boolean;

  /**
   * 尺寸
   */
  size?: "small" | "medium" | "large";

  /**
   * 是否允许半星评分
   */
  allowHalf?: boolean;

  /**
   * 用户点击评分时触发的回调
   */
  onChange?: (() => void);

  /**
   * 是否禁用
   */
  disabled?: boolean;
}

export default class Rating extends React.Component<RatingProps, any> {}
